mapper.select:
SELECT acc_id      AS account_id,
       sum(amount) AS amount_sum
FROM Interest
WHERE acc_id = #{accountId}
  AND payment_date = #{expireDate}
GROUP BY acc_id

SELECT creation_date, balance, interest_rate, preferential_interest_rate, amount
FROM Interest
WHERE acc_id = #{accountId}

mapper.delete:
delete
from interest
where branch_id = #{branchId}
  and creation_date = #{businessDate}
