mapper.select:
select rt.id as reserve_transfer_id, rt.acc_id as acc_id , rt.target_acc_id as targetAccId, rt.amount as transfer_amount, a.password as accountPassword, rt.transfer_start_time as transfer_start_time, rt.transfer_end_time as transfer_end_time, rt.retry_count as retry_count, rt.status as status, rt.transfer_date as transfer_date, rt.transfer_type as transfer_type, au.missed_count as missed_count, au.id as auto_transfer_id, a.branch_id as branch_id, a.registrant_id as registrant_id
from reserve_transfer rt join account a on a.id = rt.acc_id join auto_transfer au on rt.auto_transfer_id = au.id WHERE TO_CHAR(rt.transfer_date, 'YY/MM/DD') = TO_CHAR(SYSTIMESTAMP, 'YY/MM/DD') AND rt.status =#{status} AND TO_DATE(TO_CHAR(SYSTIMESTAMP, 'HH24:MI'), 'HH24:MI') BETWEEN TO_DATE(rt.transfer_start_time, 'HH24:MI') AND TO_DATE(rt.transfer_end_time, 'HH24:MI')
and rt.transfer_type = #{transferType}

mapper.update:
UPDATE reserve_transfer
SET status = #{status}

    ,failure_reason =  #{failureReason}

WHERE id = #{reserveTransferId}

mapper.select:
select ADD_MONTHS(next_transfer_date, missed_count)
from auto_transfer
WHERE id = #{autoTransferId}
